INSERT INTO public.course_modules (module_id, title, description, skill_level, duration, lessons_count, order_index, prerequisites, is_active) VALUES
('foundations', 'Halo Business Finance Foundations', 'Master the core business finance principles used at Halo Business Finance. This comprehensive module covers financial statement analysis, risk assessment, and decision-making frameworks essential for success in commercial lending and business finance.', 'beginner', '2.5 hours', 12, 0, '{}', true),
('capital-markets', 'Halo Capital Markets & Lending Systems', 'Explore capital markets through Halo Business Finance''s perspective. Learn how institutional funding, secondary markets, and capital allocation impact commercial lending decisions and portfolio management strategies.', 'intermediate', '3 hours', 15, 1, '{"foundations"}', true),
('risk-management', 'Advanced Risk Management & Analysis', 'Deep dive into sophisticated risk assessment methodologies used in commercial lending. Learn to identify, measure, and mitigate various types of lending risks while maintaining profitable loan portfolios.', 'intermediate', '2.5 hours', 14, 2, '{"foundations"}', true),
('regulatory-compliance', 'Banking Regulations & Compliance', 'Navigate the complex regulatory environment of commercial lending. Understand key regulations, compliance requirements, and how regulatory changes impact lending decisions and portfolio management.', 'expert', '3.5 hours', 16, 3, '{"foundations", "capital-markets"}', true),
('commercial-underwriting', 'Commercial Loan Underwriting Mastery', 'Master the art and science of commercial loan underwriting. Learn advanced analysis techniques, deal structuring, and decision-making frameworks used by top commercial lenders.', 'expert', '4 hours', 18, 4, '{"foundations", "risk-management"}', true),
('portfolio-management', 'Portfolio Management & Performance Optimization', 'Learn portfolio-level strategies for managing commercial loan portfolios. Understand diversification, risk-adjusted returns, and performance optimization techniques.', 'expert', '3 hours', 13, 5, '{"commercial-underwriting", "risk-management"}', true),
('digital-transformation', 'Digital Banking & Fintech Integration', 'Explore how digital transformation is reshaping commercial lending. Learn about fintech partnerships, automation, and technology-driven lending solutions.', 'intermediate', '2 hours', 10, 6, '{"foundations"}', true),
('leadership-development', 'Leadership in Commercial Banking', 'Develop leadership skills specific to commercial banking environments. Learn to manage teams, drive performance, and lead change initiatives in banking organizations.', 'expert', '3.5 hours', 15, 7, '{"commercial-underwriting", "portfolio-management"}', true);
